                ImageCropper(width, height, targetWidth, targetHeight, 0.8);
            }
            // 如果目标图像大小比原始图像大小要同样，但是宽度和高度之间的比例不相同，则按比例调整宽度
            else if (Math.Abs(targetWidth / width - 1) > Math.Abs(targetHeight / height - 1))
            {
                Console.WriteLine("The target image has a different aspect ratio than the original image, so we will use the limit aspect ratio fit mode");
                ImageCropper(width, height, targetWidth, targetHeight, 0.8);
            }
            else
            {
                Console.WriteLine("The target image and the original image have the same aspect ratio, so we will use the limit scale fit mode");
                ImageCropper(width, height, targetWidth, targetHeight, 0.8);
            }
        }

        static void ImageCropper(int originalWidth, int originalHeight, int targetWidth, int targetHeight, double scalingFactor)
        {
            // 创建新的图像
            Image newImage = new Bitmap(targetWidth, targetHeight);

            // 设置原始图像和目标图像的Scale 属性
            newImage.SetResolution(originalWidth, originalHeight);
            newImage.SetScaled(scalingFactor, scalingFactor);

            // 将原始图像复制到新的图像
            using (Graphics g = Graphics.FromImage(newImage))
            {
                g.DrawImage(originalImage, 0, 0, originalWidth, originalHeight);
            }

            // 保存新的图像
            newImage.Save("cropped_" + originalWidth + "_" + originalHeight + ".jpg");
        }
    }
}

