```java
import java.io.FileInputStream;
import java.io.FileOutputStream;
import java.io.IOException;

import org.apache.poi.ss.usermodel.Cell;
import org.apache.poi.ss.usermodel.Row;
import org.apache.poi.ss.usermodel.Sheet;
import org.apache.poi.ss.usermodel.Workbook;
import org.apache.poi.xssf.usermodel.XSSFWorkbook;

import db2.Clob;
import db2.Connection;
import db2.Db2Exception;
import db2.Statement;

public class XlsxToDb2 {
    public static void main(String[] args) throws Db2Exception, IOException {
        // Legge il file XLSX
        Workbook workbook = new XSSFWorkbook(new FileInputStream("percorso/allo/file.xlsx"));
        Sheet sheet = workbook.getSheetAt(0);

        // Creazione di una connessione DB2
        Connection conn = null;
        Statement stmt = null;
        try {
            conn = db2.connect("dbc:host=localhost;database=mydb", "user", "password");
            stmt = conn.createStatement();

            // Scrive il contenuto della tabella XLSX in DB2
            for (Row row : sheet) {
                Clob clob = stmt.getClob("tabelladb2");
                for (Cell cell : row) {
                    clob.append(cell.getStringCellValue() + "\n");
                }
                clob.close();
            }

            stmt.executeUpdate("COMMIT");
        } finally {
            if (stmt != null) stmt.close();
            if (conn != null) conn.close();
